import { routeRenderContext } from '../context/route-render'

const Link = ({ to, children }) => {
  const forceUpdate = React.useContext()

  const linkClickHandler = (e) => {
    e.preventDefault()
    window.history.pushState({}, '', to)    
  }

  return (
    <a onClick={linkClickHandler}>
      {children}
    </a>
  )
}

export default Link
