import React from "react";

const routeRenderContext = React.createContext()

export default RoutingRerenderProvider = ({ children }) => {
  const [toRender, setToRender ] = React.useState(0)

  const forceUpdate = () => {
    setToRender(prev => prev + 1)
  }

  return (
    <routeRenderContext.Provider value={forceUpdate}>
      {children}
    </routeRenderContext.Provider>
  )
}
